local placarImage = ""

local EXTRA_MARGIN_TOP = 0.01
local DEFAULT_TEXT_COLOR = {
    r = 255,
    g = 255,
    b = 255,
    a = 255
}
local BOARD_WIDTH = 0.03
local BOARD_HEIGHT = 0.06
local BOARD_MARGIN = 0.02
local TOP_MARGIN = 0.01
local BORDER_RADIUS = 0.012
local BORDER_THICKNESS = 0.01

Scoreboard = {}
Scoreboard.__index = Scoreboard

--- Scoreboard instance.
---@return Scoreboard
function Scoreboard:new()
    local instance = setmetatable({}, Scoreboard)
    instance.teams = {}
    return instance
end

--- Adds a new team to the scoreboard.
---@param teamName string The name of the new team.
---@param teamScore number The score of the new team.
---@param teamColor table The color of the new team (r, g, b, a).
function Scoreboard:AddTeam(teamName, teamScore, teamColor, info)
    table.insert(self.teams, {
        name = teamName,
        score = teamScore or 0,
        color = teamColor or DEFAULT_TEXT_COLOR,
        info = type(info) == 'table' and info or {}
    })
end

--- Updates the scores of the teams.
---@param teamIndex string The index of the team to update (1-based).
---@param newScore number The new score of the team.
function Scoreboard:SetScore(teamIndex, newScore)
    if self.teams[teamIndex] then
        self.teams[teamIndex].score = newScore or self.teams[teamIndex].score
    end
end

--- Creates text to display.
---@param text string The text to display.
---@param x number The x-coordinate.
---@param y number The y-coordinate.
---@param scaleH number The horizontal scale of the text.
---@param scaleW number The vertical scale of the text.
---@param r number The red color component.
---@param g number The green color component.
---@param b number The blue color component.
---@param a number The alpha color component.
---@param align number The alignment of the text.
---@param font number The font to use.
---@param minX number The minimum x-wrap.
---@param maxX number The maximum x-wrap.
---@param shadow number The shadow of the text.
function Scoreboard:CreateText(text, x, y, scaleH, scaleW, r, g, b, a, align, font, minX, maxX, shadow)
    SetTextFont(font)
    SetTextColour(r, g, b, a)
    SetTextScale(scaleH, scaleW)
    SetTextJustification(align)
    SetTextWrap(minX, maxX)
    SetTextEntry("STRING")
    AddTextComponentString(text)
    SetTextOutline()
    SetTextCentre(true)
    EndTextCommandDisplayText(x, y)
end

---Displays the scoreboard on the screen.
---This function calculates the positions for each team entry on the scoreboard and draws the background rectangle,
---team name, and team score for each team. The scoreboard is displayed at a fixed vertical position.
---@param centerVerticallyAdjustment number An optional adjustment value for vertical centering.
---@return nil
function Scoreboard:Show(centerVerticallyAdjustment)
    local screenWidth = 1.0
    local screenHeight = 1.0
    local totalHeight = #self.teams * BOARD_HEIGHT + (#self.teams - 1) * (BOARD_MARGIN + TOP_MARGIN)
    local yStart = 0.1 -- (screenHeight - totalHeight) / 2 + EXTRA_MARGIN_TOP

    local xPosition = screenWidth - BOARD_WIDTH / 2 - BOARD_MARGIN

    for i, team in ipairs(self.teams) do
        local yPosition = yStart + (i - 1) * (BOARD_HEIGHT + BOARD_MARGIN + TOP_MARGIN)

        DrawRect(xPosition, yPosition - 0.016, (BOARD_WIDTH - 0.056) * 2, 0.02, team.color.r, team.color.g,
            team.color.b, team.color.a)
        DrawSprite('placarImage', 'placarImage1', xPosition, yPosition, 0.06, 0.08, 0.0, 255, 255, 255, 255)

        self:CreateText(team.name, xPosition - 0.015, yPosition, 0.4, 0.4, DEFAULT_TEXT_COLOR.r, DEFAULT_TEXT_COLOR.g,
            DEFAULT_TEXT_COLOR.b, DEFAULT_TEXT_COLOR.a, 1, 4, 0.0, 1.0, 0)
        self:CreateText(tostring(team.score), xPosition + 0.015, yPosition - 0.005, 0.6, 0.6, DEFAULT_TEXT_COLOR.r,
            DEFAULT_TEXT_COLOR.g, DEFAULT_TEXT_COLOR.b, DEFAULT_TEXT_COLOR.a, 1, 2, 0.0, 1.0, 0)

    end
end

---Builds the scoreboard by creating a runtime texture and continuously showing the scoreboard.
---@return nil
function Scoreboard:build()
    if self.threadBuild then
        return
    end
    self.threadBuild = true

    local resourceName = GetCurrentResourceName()
    local fileCache = LoadResourceFile(resourceName, 'Images/character.png')
    if fileCache then

    end

    Citizen.CreateThread(function()
        local txd = CreateRuntimeTxd('placarImage')
        local tex = CreateRuntimeTexture(txd, 'placarImage1', 256, 256)
        SetRuntimeTextureImage(tex, placarImage)
        while self.threadBuild do
            self:Show()
            Citizen.Wait(0)
        end
    end)
end

---Stops the scoreboard update thread.
---@return nil
function Scoreboard:stop()
    self.threadBuild = false
end

---Clears the scoreboard by removing all team entries.
---@return nil
function Scoreboard:clear()
    self.teams = {}
end

